<script setup>
import { useStore } from "~/store";
const store = useStore;
import axios from "axios";
import { createToast } from "mosha-vue-toastify";
import "mosha-vue-toastify/dist/style.css";
import Cookies from "js-cookie";
const tokenCookie = Cookies.get("token");
let activeNav = ref(false);
let activeItemsContainer = ref(false);

const localePath = useLocalePath();
const { locale, setLocale } = useI18n();

let overlay = ref(false);
let route = useRoute();
const changeLang = async () => {
  locale.value = locale.value === "en" ? "ar" : "en";
  if (locale.value == "ar") {
    setLocale("ar");
    useHead({
      htmlAttrs: {
        lang: "ar",
        dir: "rtl",
      },
    });
  } else {
    useHead({
      htmlAttrs: {
        lang: "en",
        dir: "ltr",
      },
    });
    setLocale("en");
  }
  console.log(route);
  const query = useRoute().query;
  await navigateTo(
    localePath({ path: useRoute().path, query: query }, undefined, {
      preserveQuery: true,
    })
  );
};

const updateLang = () => {
  if (locale.value == "ar") {
    setLocale("ar");
    useHead({
      htmlAttrs: {
        lang: "ar",
        dir: "rtl",
      },
    });
  } else if (locale.value == "en") {
    useHead({
      htmlAttrs: {
        lang: "en",
        dir: "ltr",
      },
    });
    setLocale("en");
  }
  if (process.client) {
    const navItems = document.querySelectorAll(".nav-link");

    navItems.forEach((item) => {
      item.addEventListener("click", () => {
        const navbarCollapse = document.querySelector(".navbar-collapse");
        navbarCollapse.classList.remove("show");
        document.querySelector(".navbar-toggler").classList.add("collapsed");
      });
    });
  }
};

let footerData = ref();

const getFooterData = async () => {
  let result = await axios.get(`${getUrl()}/footer`, {
    headers: {
      "Content-Language": `${locale.value}`,
    },
  });

  if (result.status == 200) {
    footerData.value = result.data.data;
  }
};

let email = ref("");
let error = ref();

let title = ref("تم الاشتراك بنجاح");

if (locale.value == 'ar') {
  title.value = 'تم الاشتراك بنجاح';
} else {
  title.value = 'You have successfully subscribed'
}
const subscripe = async () => {
  if (email.value != "") {
    try {
      let result = await axios.post(
        `${getUrl()}/subscriber/store`,
        { email: email.value },
        {
          headers: {
            "Content-Language": `${locale.value}`,
          },
        }
      );

      if (result.status >= 200) {
        error.value = "";
        console.log("dasdas");
        createToast(
          {
            title: title.value,
          },
          {
            type: "success",
            transition: "bounce",
            showIcon: "true",
            timeout: 3000,
            toastBackgroundColor: "#dcb63b",
          }
        );
      }
    } catch (errors) {
      if (errors.response) {
        error.value = errors.response.data.errors;
      }
    }
  }
};

let checkInt = ref(false);
onMounted(() => {
  window.addEventListener("online", function () {
    checkInt.value = false;
  });
  window.addEventListener("offline", function () {
    checkInt.value = true;
  });

  updateLang();
  window.addEventListener("scroll", function () {
    if (this.window.scrollY >= 300) {
      activeNav.value = true;
      activeItemsContainer.value = true;
    } else if (window.scrollY == 0) {
      activeNav.value = false;
      activeItemsContainer.value = false;
    }
  });
  getFooterData();
  
  store.dispatch("loadBasketFromLocalStorage");
});

onBeforeMount(() => {
  store.dispatch("loadBasketFromLocalStorage");
  updateLang();
});
</script>

