<script setup>
import { Vue3Lottie } from "vue3-lottie";
import loader from "~/assets/animations/Loader.json";
import cart from "~/assets/animations/car2.json";
import axios from "axios";
import { useStore } from "~/store";
import Cookies from "js-cookie";
const tokenCookie = ref(Cookies.get("token") ? Cookies.get("token") : null);
let store = useStore;
let user = store.state.user
const localePath = useLocalePath();
const { locale } = useI18n();
let pendingLoader = ref(false);
let pendingLoader2 = ref(false);
let pendingLoader3 = ref(false);

let orderNumber = ref('');

let orders1 = ref([]);
let type = ref('processing');
let orders2 = ref([]);

let desc = ref('');
const getDesc = async () => {
       let result = await axios.get(`${getUrl()}/allsettings`, {
        headers: {
            "Content-Language": `${locale.value}`,
        },
       });
    desc.value = result.data.data.followyourOrder;
}
const getOrders = async () => {
    if (tokenCookie.value) {
        pendingLoader.value = true;
        pendingLoader2.value = true;
        pendingLoader3.value = false;
        orders1.value = [];
        let result = await axios.get(`${getUrl()}/requests_auth`, {
            params: {
                order_number: orderNumber.value,
                filter: type.value
            },
            headers: {
                "Content-Language": `${locale.value}`,
                Authorization: `Bearer ${tokenCookie.value}`,
            },
        });
        if (result.status == 200) {
            pendingLoader.value = false;
            pendingLoader2.value = false;
            if(result.data.data.length < 1){
                pendingLoader3.value = true;
            }
        }
        orders1.value = result.data.data;
    } else {
        pendingLoader.value = true;
        pendingLoader2.value = true;
        pendingLoader3.value = false;
        orders1.value = [];
        let result = await axios.get(`${getUrl()}/requests-search`, {
            params: {
                order_number: parseInt(orderNumber.value)
            },
            headers: {
                "Content-Language": `${locale.value}`,

            },
        });
        if (result.status == 200) {
            pendingLoader.value = false;
            pendingLoader2.value = false;
            if(result.data.data.length < 1){
                pendingLoader3.value = true;
            }
        }
        orders1.value = result.data.data;

    }
}
let chooseBtn = ref(1);


useHead({
  title: locale.value == 'ar' ? 'الطلبات' : 'orders'
});

let items = ref([
    {
        title: locale.value == "ar" ? "الرئيسية" : "home",
        ddisabled: false,
        class: "breadcrumbs-text",
        href: "/",
    },
    {
        title: locale.value == "ar" ? "تتبع طلبك" : "follow your order",
        disabled: true,
        class:"breadcrumbs-dark",
        href: "orders",
    },
]);

onMounted(() => {
    console.log(user);
    if (tokenCookie.value) {
        getOrders();
    }
    getDesc();
})

</script>

